
  create table "public"."feedback" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "name" text,
    "email" text,
    "grund" text,
    "nachricht" text
      );


alter table "public"."feedback" enable row level security;


  create table "public"."pdf_files" (
    "id" uuid not null default gen_random_uuid(),
    "user_id" uuid,
    "file_path" text not null,
    "date_of_absence" text not null,
    "created_at" timestamp without time zone default now(),
    "reason" text default ''::text,
    "pdf_name" text
      );


alter table "public"."pdf_files" enable row level security;


  create table "public"."profiles" (
    "id" uuid not null,
    "first_name" text,
    "last_name" text,
    "birthday" text,
    "class" text,
    "calendar_url" text,
    "first_name_trainer" text,
    "last_name_trainer" text,
    "phone_number_trainer" text,
    "email_trainer" text,
    "onboarding_completed" boolean default false,
    "isFullNameEnabled" boolean not null default true,
    "isFullSubjectEnabled" boolean not null default true,
    "isDoNotSaveEnabled" boolean not null default false,
    "is_encrypted" boolean default false,
    "encrypted_data" text,
    "encryption_salt" text,
    "created_at" timestamp with time zone default now(),
    "updated_at" timestamp with time zone default now(),
    "has_pin" boolean not null default false,
    "pin_hash" text
      );


alter table "public"."profiles" enable row level security;

CREATE UNIQUE INDEX feedback_pkey ON public.feedback USING btree (id);

CREATE UNIQUE INDEX pdf_files_pkey ON public.pdf_files USING btree (id);

CREATE UNIQUE INDEX profiles_pkey ON public.profiles USING btree (id);

alter table "public"."feedback" add constraint "feedback_pkey" PRIMARY KEY using index "feedback_pkey";

alter table "public"."pdf_files" add constraint "pdf_files_pkey" PRIMARY KEY using index "pdf_files_pkey";

alter table "public"."profiles" add constraint "profiles_pkey" PRIMARY KEY using index "profiles_pkey";

alter table "public"."profiles" add constraint "profiles_id_fkey" FOREIGN KEY (id) REFERENCES auth.users(id) ON DELETE CASCADE not valid;

alter table "public"."profiles" validate constraint "profiles_id_fkey";

set check_function_bodies = off;

CREATE OR REPLACE FUNCTION public.add_generated_absence()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    INSERT INTO generated_absences (success) 
    VALUES (true);  -- Ersetzen Sie NEW.column1 und NEW.column2 durch die tats√§chlichen Spaltennamen
    RETURN NEW;
END;
$function$
;

CREATE OR REPLACE FUNCTION public.count_generated_absences()
 RETURNS bigint
 LANGUAGE plpgsql
AS $function$
BEGIN
    RETURN (SELECT COUNT(*) FROM public.generated_absences);
END;
$function$
;

CREATE OR REPLACE FUNCTION public.handle_new_user()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO ''
AS $function$begin 
    insert into public.profiles (id) 
    values (new.id);   
    
    return new; 
end;$function$
;

CREATE OR REPLACE FUNCTION public.insert_generated_absence()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    INSERT INTO public.generated_absences (success) 
    VALUES (true);
    
    RETURN NEW;
END;
$function$
;

