  create policy "Allow everyone to insert feedback"
  on "public"."feedback"
  as permissive
  for insert
  to authenticated, anon
with check (true);



  create policy "Enable insert for users based on user_id"
  on "public"."pdf_files"
  as permissive
  for insert
  to public
with check ((( SELECT auth.uid() AS uid) = user_id));



  create policy "Enable users to delete their own data only"
  on "public"."pdf_files"
  as permissive
  for delete
  to authenticated
using ((( SELECT auth.uid() AS uid) = user_id));



  create policy "Enable users to view their own data only"
  on "public"."pdf_files"
  as permissive
  for select
  to authenticated
using ((( SELECT auth.uid() AS uid) = user_id));



