CREATE TRIGGER pdf_files_insert_trigger AFTER INSERT ON public.pdf_files FOR EACH ROW EXECUTE FUNCTION public.add_generated_absence();
ALTER TABLE "public"."pdf_files" DISABLE TRIGGER "pdf_files_insert_trigger";

CREATE TRIGGER on_auth_user_created AFTER INSERT ON auth.users FOR EACH ROW EXECUTE FUNCTION public.handle_new_user();

